# -*- coding:utf-8 -*-"""Created at 2:05 PM 1/10/2020This is used to put files from local path to jump server with paramiko@author: guangqiang.lu"""import osimport paramikohost = '18.210.190.6'port = 22username = 'guangqiang.lu'password = 'LUgq1990~'local_path = 'C:/Users/guangqiiang.lu/Documents/lugq/github/mysch'target_path = 'hr_project/source_code/mysch'def create_sftp():    ssh = paramiko.SSHClient()    ssh.set_missing_host_key_policy(paramiko.AutoAddPolicy())    # connect    ssh.connect(host, port=port, username=username, password=password, look_for_keys=False)    sftp = ssh.open_sftp()    return sftpdef put_files(single_file=False, local_single_file_name=None):    sftp = create_sftp()    if single_file:        if local_single_file_name:            raise ValueError("Please provide with single server path!")        sftp.put(os.path.join(local_path, local_single_file_name), os.path.join(target_path, local_single_file_name))    else:        # Here we should get whole files and folder        os.walk()