# -*- coding:utf-8 -*-"""Created at 3:48 PM 1/9/2020This is to implement with python keyboard interrupt error that we couldhandle use cancel even with our define logic@author: guangqiang.lu"""import timeimport sysimport datetimeimport osdef main():    while True:        print("Now is:", datetime.datetime.now())        time.sleep(2)if __name__ == '__main__':    try:        main()    except KeyboardInterrupt:        print("User wants to stop the program.")        # we should really consider whether or not we should exit the thread!        # try:        #     sys.exit(0)        # except SystemExit as e:        #     os._exit(0)